var testconfig = require('../lib/testconfig')

// https://github.com/haraldrudell/mochawrapper
var assert = require('mochawrapper')

exports['TestConfig:'] = {
	'Exports': function () {
		assert.exportsTest(testconfig, 2)
	},
	'GetTestConfig': function () {
		testconfig.getTestConfig(__dirname)
	},
	'GetTestConfig Dirname Error': function () {
		assert.throws(function () {
			testconfig.getTestConfig()
			, /__dirname/})
	},
}
